function writeFile(a,b)node.egc.setmode(node.egc.ALWAYS)local c=file.open(a,"w")if c then local d=c:write(sjson.encode(b))if d==nil then c:close()return false end;c:close()c,d=nil,nil end;return true end;function readFile(a)node.egc.setmode(node.egc.ALWAYS)if file.exists(a)then local e=file.open(a,"r")local f=file.stat(a)local g=e:read(f.size)e:close()e,f=nil,nil;return g else return nil end end
